import React, { useEffect, useState, useContext } from 'react';
import AuthContext from '../context/AuthContext';
import EmotionDetection from './EmotionDetection';

const RealTimePessimism = ({ interactionType, attempts, errors, correct, showVideo }) => {
  const { authTokens } = useContext(AuthContext);
  const [pessimismLevel, setPessimismLevel] = useState(null);
  const [emotion, setEmotion] = useState('');

  useEffect(() => {
    const socket = new WebSocket('ws://localhost:8000/ws/pessimism_detection/');

    socket.onopen = () => {
      console.log('WebSocket connected');
    };

    socket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      setPessimismLevel(data.pessimism_level);
    };

    socket.onclose = () => {
      console.log('WebSocket disconnected');
    };

    const sendData = () => {
      if (socket.readyState === WebSocket.OPEN) {
        const interactionData = {
          token: authTokens.access,
          interaction_type: interactionType,
          attempts: attempts,
          errors: errors,
          correct: correct,
          emotion: emotion,
        };
        socket.send(JSON.stringify(interactionData));
      }
    };

    const intervalId = setInterval(sendData, 5000); // Send data every 5 seconds

    return () => {
      clearInterval(intervalId);
      socket.close();
    };
  }, [authTokens, interactionType, attempts, errors, correct, emotion]);

  return (
    <div>
      <h1>Real-Time Pessimism Detection</h1>
      <p>Pessimism Level: {pessimismLevel}</p>
      {showVideo && <EmotionDetection setEmotion={setEmotion} />}
    </div>
  );
};

export default RealTimePessimism;
