import React, { useRef, useEffect } from 'react';
import * as faceapi from 'face-api.js';

const EmotionDetection = ({ setEmotion }) => {
  const videoRef = useRef(null);

  useEffect(() => {
    const loadModels = async () => {
      const MODEL_URL = '/models';
      await faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL);
      await faceapi.nets.faceExpressionNet.loadFromUri(MODEL_URL);
    };

    const startVideo = () => {
      navigator.mediaDevices.getUserMedia({ video: {} })
        .then((stream) => {
          videoRef.current.srcObject = stream;
        })
        .catch((err) => {
          console.error('Error accessing webcam: ', err);
        });
    };

    const handleVideoOnPlay = () => {
      setInterval(async () => {
        if (videoRef.current) {
          const detections = await faceapi.detectAllFaces(
            videoRef.current,
            new faceapi.TinyFaceDetectorOptions()
          ).withFaceExpressions();

          if (detections.length > 0) {
            const emotions = detections[0].expressions;
            const maxEmotion = Object.keys(emotions).reduce((a, b) => emotions[a] > emotions[b] ? a : b);
            setEmotion(maxEmotion);
          }
        }
      }, 1000);
    };

    const initialize = async () => {
      await loadModels();
      startVideo();
      videoRef.current.addEventListener('play', handleVideoOnPlay);
    };

    initialize();

    return () => {
      if (videoRef.current) {
        videoRef.current.removeEventListener('play', handleVideoOnPlay);
        if (videoRef.current.srcObject) {
          videoRef.current.srcObject.getTracks().forEach(track => track.stop());
        }
      }
    };x
  }, [setEmotion]);

  return (
    <div>
      <video ref={videoRef} autoPlay muted style={{ display: 'none' }} />
    </div>
  );
};

export default EmotionDetection;
