import React, { useRef, useEffect, useState } from 'react';
import * as faceapi from 'face-api.js';

const EmotionDetection = ({ setEmotion }) => {
  const videoRef = useRef(null);
  const socketRef = useRef(null);
  const [initialized, setInitialized] = useState(false);

  useEffect(() => {
    const loadModels = async () => {
      const MODEL_URL = '/models';
      await faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL);
      await faceapi.nets.faceExpressionNet.loadFromUri(MODEL_URL);
      console.log("Models loaded");
    };

    const startVideo = () => {
      navigator.mediaDevices.getUserMedia({ video: {} })
        .then((stream) => {
          videoRef.current.srcObject = stream;
        })
        .catch((err) => {
          console.error('Error accessing webcam: ', err);
        });
    };

    const handleVideoOnPlay = () => {
      setInterval(async () => {
        if (videoRef.current && initialized) {
          const detections = await faceapi.detectAllFaces(
            videoRef.current,
            new faceapi.TinyFaceDetectorOptions()
          ).withFaceExpressions();
          if (detections.length > 0) {
            const emotions = detections[0].expressions;
            console.log("Detected emotions:", emotions);
            const maxEmotion = Object.keys(emotions).reduce((a, b) => emotions[a] > emotions[b] ? a : b);
            console.log("Max emotion:", maxEmotion);
            setEmotion(maxEmotion);
            sendData(maxEmotion);
          } else {
            console.log("No face detected");
          }
        }
      }, 1000);
    };

    const initialize = async () => {
      await loadModels();
      startVideo();
      videoRef.current.addEventListener('play', handleVideoOnPlay);
      setInitialized(true);
    };

    initialize();

    return () => {
      if (videoRef.current) {
        videoRef.current.removeEventListener('play', handleVideoOnPlay);
        if (videoRef.current.srcObject) {
          videoRef.current.srcObject.getTracks().forEach(track => track.stop());
        }
      }
    };
  }, [setEmotion, initialized]);

  const sendData = (emotion) => {
    if (socketRef.current && socketRef.current.readyState === WebSocket.OPEN) {
      socketRef.current.send(JSON.stringify({ emotion }));
    } else {
      console.log("WebSocket is not open. Ready state:", socketRef.current.readyState);
    }
  };

  useEffect(() => {
    socketRef.current = new WebSocket('ws://localhost:8000/ws/pessimism_detection/');
    socketRef.current.onopen = () => {
      console.log('WebSocket connected');
    };
    socketRef.current.onclose = () => {
      console.log('WebSocket disconnected');
    };

    return () => {
      if (socketRef.current) {
        socketRef.current.close();
      }
    };
  }, []);

  return (
    <div>
      <video ref={videoRef} autoPlay muted style={{ display: 'none' }} />
    </div>
  );
};

export default EmotionDetection;
